INSERT INTO `tbl_product` VALUES (26, 8, '', NULL, 0, NULL, 'GP12X', 'YAMAHA MGP12X', 'yamaha-mgp12x', '', NULL, '', '[\"http://localhost/nhacviethanoi/medias/products/YAMAHA-MGP12X/mgp12x-1-600x600.jpg\",\"http://localhost/nhacviethanoi/medias/products/YAMAHA-MGP12X/MGP12X-d-600x399.jpg\",\"http://localhost/nhacviethanoi/medias/products/YAMAHA-MGP12X/MGP12X-c-600x576.jpg\",\"http://localhost/nhacviethanoi/medias/products/YAMAHA-MGP12X/MGP12X-b-1-600x262.jpg\",\"http://localhost/nhacviethanoi/medias/products/YAMAHA-MGP12X/MGP12X-a-600x200.jpg\"]', 'Yamaha MGP12X là sản phẩm mixer 12 kênh hiện đại với một bộ tính năng đáp ứng các nhu cầu của các ban nhạc hiện nay lưu diễn, các nhóm hoạt động, hội trường thị trấn..vvv Giao diện điều khiển có tổng cộng 12 kênh đầu vào với 4 đầu vào mono và 4 đầu vào âm thanh nổi, bộ xử lý hiệu ứng kép, nén 1 núm, các kênh hybrid stereo và phát lại âm thanh / điều khiển tùy chọn từ Apple iPad và iPhone.', '<p style=\"box-sizing: border-box; margin-bottom: 1.3em; margin-top: 0px; color: #505050; font-family: Roboto, sans-serif; font-size: 14.5px;\"><span style=\"box-sizing: border-box;\"><a style=\"box-sizing: border-box; background-color: transparent; touch-action: manipulation; color: #3d3d3d; text-decoration-line: none;\" href=\"http://demo.nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X.jpg\"><img class=\"size-medium wp-image-1916 aligncenter lazy-load-active\" style=\"box-sizing: border-box; border-style: none; max-width: 100%; height: auto; display: block; vertical-align: middle; transition: opacity 1s ease 0s; opacity: 1; clear: both; margin: 0px auto;\" src=\"https://demo.nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-217x300.jpg\" sizes=\"(max-width: 217px) 100vw, 217px\" srcset=\"https://nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-217x300.jpg 217w, https://nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X.jpg 542w\" alt=\"\" width=\"217\" height=\"300\" data-src=\"https://demo.nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-217x300.jpg\" data-srcset=\"https://nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-217x300.jpg 217w, https://nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X.jpg 542w\" /></a>Đặc t&iacute;nh nổi bật của Yamaha MGP12X</span></p>\r\n<ul style=\"box-sizing: border-box; list-style-position: initial; list-style-image: initial; margin-top: 0px; padding: 0px; margin-bottom: 1.3em; color: #505050; font-family: Roboto, sans-serif; font-size: 14.5px;\">\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">C&aacute;c mic preamp chất lượng ph&ograve;ng thu D-PRE với mạch Inverted Darlington.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Bộ xử l&yacute; EQ X-pressive dựa tr&ecirc;n kỹ thuật VCM của Yamaha.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Bộ xử l&yacute; compressor 1-knob chuy&ecirc;n nghiệp</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Những bộ hiệu ứng digital cao cấp: REV-X v&agrave; Classic SPX.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Channel hybrid stereo với những bộ xử l&yacute; DSP mạnh mẽ.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Kết nối digital cho iPod/iPhone.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Ứng dụng MGP Editor để điều chỉnh chi tiết những thiết lập DSP tr&ecirc;n mixer iPod/iPhone.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Khung m&aacute;y hợp kim, chống va đập.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Dễ d&agrave;ng gắn l&ecirc;n tủ rack</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Nguồn điện sử dụng đa dạng</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">6 mic input c&oacute; hỗ trợ cấp nguồn Phantom 48V v&agrave; bộ lọc HPF ở mỗi channel.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">Bổ sung 2TR input để nhận những t&iacute;n hiệu từ c&aacute;c thiết bị analog hoặc iPod/iPhone.</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">12 Line Input (8 mono v&agrave; 4 stereo)</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">4 GROUP bus + ST bus</li>\r\n<li style=\"box-sizing: border-box; margin-bottom: 0.6em; margin-left: 1.3em;\">2 AUX Send + 2 FX Send</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin-bottom: 1.3em; margin-top: 0px; color: #505050; font-family: Roboto, sans-serif; font-size: 14.5px;\"><a style=\"box-sizing: border-box; background-color: transparent; touch-action: manipulation; color: #3d3d3d; text-decoration-line: none;\" href=\"http://demo.nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-a.jpg\"><img class=\"size-medium wp-image-1918 aligncenter lazy-load-active\" style=\"box-sizing: border-box; border-style: none; max-width: 100%; height: auto; display: block; vertical-align: middle; transition: opacity 1s ease 0s; opacity: 1; clear: both; margin: 0px auto;\" src=\"https://demo.nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-a-300x100.jpg\" sizes=\"(max-width: 300px) 100vw, 300px\" srcset=\"https://nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-a-300x100.jpg 300w, https://nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-a-768x256.jpg 768w, https://nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-a-600x200.jpg 600w, https://nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-a.jpg 900w\" alt=\"\" width=\"300\" height=\"100\" data-src=\"https://demo.nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-a-300x100.jpg\" data-srcset=\"https://nhacviethanoi.com/wp-content/uploads/2020/08/MGP12X-a-300x100.jpg 300w, https://nhacviethanoi.com/wp-content/uploads/2020/