INSERT INTO `tbl_product` VALUES (30, 8, '', NULL, 0, NULL, 'MG10XUF', 'Yamaha MG10XUF', 'yamaha-mg10xuf', '', NULL, '', '[\"http://localhost/nhacviethanoi/medias/products/Yamaha-MG10XUF/10XUF-d-600x545.jpg\",\"http://localhost/nhacviethanoi/medias/products/Yamaha-MG10XUF/10XUF-b-3.jpg\",\"http://localhost/nhacviethanoi/medias/products/Yamaha-MG10XUF/10XUF-a.jpg\",\"http://localhost/nhacviethanoi/medias/products/Yamaha-MG10XUF/10XUF-600x600.jpg\",\"http://localhost/nhacviethanoi/medias/products/Yamaha-MG10XUF/10XUF-1-600x600.jpg\"]', 'Đến từ thương hiệu Nhật Bản, người dùng không còn phải bận tâm đến chất lượng hay độ bền sản phẩm nữa. Yamaha MG series luôn là sự lựa chọn mang đến sự an tâm hàng đầu trong tầm giá.', '<h2 style=\"box-sizing: border-box; color: #0a0a0a; width: 1041.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.6em; line-height: 1.3; font-family: Roboto, sans-serif; font-weight: 500;\"><span style=\"box-sizing: border-box;\">Mixer Yamaha MG10XUF</span></h2>\r\n<p style=\"box-sizing: border-box; margin-bottom: 1.3em; margin-top: 0px; color: #505050; font-family: Roboto, sans-serif; font-size: 14.5px;\"><em style=\"box-sizing: border-box;\">Mixer Yamaha MG10XUF</em>10 k&ecirc;nh mang theo truyền thống &acirc;m thanh tuyệt vời của&nbsp;<em style=\"box-sizing: border-box;\">&acirc;m thanh amaha</em>, cung cấp đầu đọc microphon .&nbsp;<a style=\"box-sizing: border-box; background-color: transparent; touch-action: manipulation; color: #3d3d3d; text-decoration-line: none;\" title=\"Bộ trộn Analog MG Series + MG Series XU\" href=\"https://amnhacyamaha.vn/bo-tron-analog-mg-series-mg-series-xu.html\" target=\"_blank\" rel=\"noopener noreferrer\">Bộ trộn Analog MG Series + MG Series XU</a>&nbsp;n&agrave;y&nbsp;c&oacute; 10 k&ecirc;nh với 4 đầu v&agrave;o micr&ocirc;, mỗi đầu v&agrave;o c&oacute; đầu ra rời rạc loại A-D &ldquo;PRE-D&rdquo; ri&ecirc;ng biệt của Yamaha để mang lại tỷ lệ nhiễu / thu nhỏ cao nhất c&oacute; thể.</p>\r\n<p style=\"box-sizing: border-box; margin-bottom: 1.3em; margin-top: 0px; color: #505050; font-family: Roboto, sans-serif; font-size: 14.5px;\">&Acirc;m thanh độ ph&acirc;n giải cao của tiền khuyếch đại D-PRE l&agrave; một t&iacute;nh năng y&ecirc;u th&iacute;ch của m&aacute;y trộn chuy&ecirc;n nghiệp của Yamaha, đ&aacute;p ứng c&aacute;c y&ecirc;u cầu của c&aacute;c kỹ sư &acirc;m thanh khắt khe nhất. Với &acirc;m lớn, bass tự nhi&ecirc;n, một &acirc;m tầm trung gi&agrave;u c&oacute;, v&agrave; mức cao-tất cả mịn với tiền khuyếch m&eacute;o-D-PRE rất thấp cung cấp một dải tần số ấn tượng rộng để xử l&yacute; t&iacute;n hiệu từ bất kỳ nguồn &acirc;m thanh một c&aacute;ch trung thực giữ lại sắc th&aacute;i chi tiết nhất về bản gốc &acirc;m thanh.</p>\r\n<p style=\"box-sizing: border-box; margin-bottom: 1.3em; margin-top: 0px; color: #505050; font-family: Roboto, sans-serif; font-size: 14.5px;\"><img class=\"aligncenter lazy-load-active\" style=\"box-sizing: border-box; border-style: none; max-width: 100%; height: auto; display: block; vertical-align: middle; transition: opacity 1s ease 0s; opacity: 1; clear: both; margin: 0px auto;\" src=\"https://amnhacyamaha.vn/media/product/1043_mixer_yamaha_mg10xuf.jpg\" alt=\"Mixer Yamaha MG10XUF\" width=\"700\" height=\"700\" data-src=\"https://amnhacyamaha.vn/media/product/1043_mixer_yamaha_mg10xuf.jpg\" /></p>\r\n<p style=\"box-sizing: border-box; margin-bottom: 1.3em; margin-top: 0px; color: #505050; font-family: Roboto, sans-serif; font-size: 14.5px;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin-bottom: 1.3em; margin-top: 0px; color: #505050; font-family: Roboto, sans-serif; font-size: 14.5px;\">Bạn c&oacute; thể ghi trực tiếp l&ecirc;n iPad (2 hoặc mới hơn) th&ocirc;ng qua kết nối USB v&agrave; sử dụng Bộ kết nối M&aacute;y ảnh Apple iPad / Bộ chuyển đổi Lightning sang USB Camera .Khung gầm kim loại, vững chắc, được x&acirc;y dựng để trở th&agrave;nh đối t&aacute;c ho&agrave;n hảo tr&ecirc;n đường. Mỗi