INSERT INTO `tbl_product` VALUES (31, 8, '', NULL, 0, NULL, 'MG06X', 'Yamaha MG06X', 'yamaha-mg06x', '', NULL, '', '[\"http://localhost/nhacviethanoi/medias/products/Yamaha-MG06X/06.jpg\",\"http://localhost/nhacviethanoi/medias/products/Yamaha-MG06X/06-a.jpg\",\"http://localhost/nhacviethanoi/medias/products/Yamaha-MG06X/06-600x600.jpg\"]', 'Đến từ thương hiệu Nhật Bản, người dùng không còn phải bận tâm đến chất lượng hay độ bền sản phẩm nữa. Yamaha MG series luôn là sự lựa chọn mang đến sự an tâm hàng đầu trong tầm giá.', '<h2 style=\"box-sizing: border-box; color: #0a0a0a; width: 1041.5px; margin-top: 0px; margin-bottom: 0.5em; text-rendering: optimizespeed; font-size: 1.6em; line-height: 1.3; font-family: Roboto, sans-serif; font-weight: 500;\"><span style=\"box-sizing: border-box;\">Mixer Yamaha MG06X</span></h2>\r\n<p style=\"box-sizing: border-box; margin-bottom: 1.3em; margin-top: 0px; color: #505050; font-family: Roboto, sans-serif; font-size: 14.5px;\">Yamaha MG06X rất ph&ugrave; hợp cho c&aacute;c gi&agrave;n trộn si&ecirc;u nhỏ gọn, với c&aacute;c t&iacute;nh năng phong ph&uacute; v&agrave; chất lượng &acirc;m thanh vượt trội. Op-amp chất lượng của Yamaha MG06X đảm bảo rằng bản phối của bạn nghe trong suốt v&agrave; r&otilde; r&agrave;ng. C&aacute;c tiền khuếch đại D-PRE trong suốt như pha l&ecirc; của MG06X sẽ vắt kiệt từng giọt &acirc;m thanh từ mic của bạn, với &acirc;m trầm tự nhi&ecirc;n v&agrave; &acirc;m cao mượt m&agrave;. Với c&aacute;c hiệu ứng kỹ thuật số tr&ecirc;n bo mạch, năng lượng ảo c&oacute; thể chuyển đổi, c&ocirc;ng tắc pad, EQ v&agrave; đo s&aacute;ng LED, Yamaha MG06X c&oacute; thể đảm nhận bất kỳ nhiệm vụ n&agrave;o bạn thực hiện. Sử dụng nhiều t&iacute;nh năng hiện đại từ bảng điều khiển cao cấp của Yamaha.</p>\r\n<p style=\"box-sizing: border-box; margin-bottom: 1.3em; margin-top: 0px; color: #505050; font-family: Roboto, sans-serif; font-size: 14.5px;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin-bottom: 1.3em; margin